const header=document.getElementById("header"),nextBtn=document.getElementById("right"),prevBtn=document.getElementById("left"),sectionNew=document.querySelectorAll(".sectionNew");function isScrolled(e){window.scrollY>e?header.classList.add("scrolled"):header.classList.remove("scrolled")}window.addEventListener("scroll",()=>{isScrolled(0)});let currentIndex=0;function updateCarousel(){let e=-(100*currentIndex);sectionNew.forEach(t=>{t.style.transform=`translateX(${e}%)`})}nextBtn.addEventListener("click",()=>{currentIndex<sectionNew.length-1?currentIndex++:currentIndex=0,updateCarousel()}),prevBtn.addEventListener("click",()=>{currentIndex>0?currentIndex--:currentIndex=sectionNew.length-1,updateCarousel()});