const header=document.getElementById("header"),nextBtn=document.getElementById("right"),prevBtn=document.getElementById("left"),sectionNew=document.querySelectorAll(".sectionNew");function isScrolled(e){window.scrollY>e?header.classList.add("scrolled"):header.classList.remove("scrolled")}document.getElementById("miIframe").addEventListener("load",function(){for(var e=(document.getElementById("miIframe").contentDocument||document.getElementById("miIframe").contentWindow.document).getElementsByTagName("script"),t=0;t<e.length;t++)e[t].setAttribute("async",!0)}),window.addEventListener("scroll",()=>{isScrolled(0)});let currentIndex=0;function updateCarousel(){let e=-(100*currentIndex);sectionNew.forEach(t=>{t.style.transform=`translateX(${e}%)`})}nextBtn.addEventListener("click",()=>{currentIndex<sectionNew.length-1?currentIndex++:currentIndex=0,updateCarousel()}),prevBtn.addEventListener("click",()=>{currentIndex>0?currentIndex--:currentIndex=sectionNew.length-1,updateCarousel()});